#!/bin/bash
fle=`find *.txt -printf "%T+ %p\n" | sort -r | sed -e 's/^.\+[0-9]\{10\} \(.\+\)$/\1/g' | tr '\n' '\0' |xargs -0 tail -n +1 | sed 's/ <==/>/g' | tr '\n' ' ** '|  sed 's/==> /\n/g' | fzf -e --multi --delimiter=.txt --tiebreak=begin --layout=reverse --preview='echo {1};echo "---------------------------";cat {1}.txt' --preview-window=right:50%:wrap | grep ".+?\.txt>" -o -P | sed 's/txt>/txt/g'`
